import React, { useState, useEffect } from 'react';

const Quote = ({props}) => {
  useEffect(() => {
    console.log(props)
  },[props])
  return (
    <>
      <p className='text-4xl text-black font-bold'>"{props.quote}"</p>
      <p className='text-md text-right font-bold right-10'>- {props.quoter}</p>
    </>
  )
}

export default function Quotes({quote}) {
  return (
    <div className='w-[100%] bg-cover h-96 grid justify-center content-center mt-1 p-10 quote-border justify-self-center'>
      { quote && quote.data ? <Quote props={quote.data} /> : <p className='text-4xl text-black font-bold'> Select a character below </p>}
    </div>
  )
}
