import React, { useState, useEffect } from 'react';

export default function Character() {
  const images = require.context('../../public/character_images', true);
  const characters = ['arya_stark','cersei_lannister','daenerys_targaryen','hodor','jon_snow','lord_varys','oberyn_martell','petyr_baelish','tyrion_lannister']

  const [selected, setSelected] = useState('');

  useEffect(() => {
    if(selected.target) console.log(selected.target.id)
  }, [selected])

  return (
    <div className='flex flex-wrap justify-center mt-1 justify-around'>
      {characters.map((char) => {
        if(char !== 'hodor'){
          const sprite = images(`./${char}.png`);
          return (
            <img src={sprite} id={char} key={char} className='object-cover h-64 cursor-pointer' onClick={(char) => setSelected(char)}/>
          )
        }
      })}
    </div>
  )
}
