import React, { useState, useEffect } from 'react';
import Quotes from './Quotes.js';
import Characters from './Characters.js';
import QuoteService from '../services/quote_service.js';

export default function Content() {
  const [character, setCharacter] = useState('');
  const [quote, setQuote] = useState({});
  const [error, setError] = useState(false);
  const [random, setRandom] = useState(true);
  const [loading, setLoading] = useState(false);

  useEffect(()=>{
    if(character.target){
      setLoading(true);
      QuoteService.quoteByChar(character.target.id).then((res) => {
        setQuote(res)
      }).then(()=>{
        setLoading(false)
      }).catch((err)=>{
        setError(true)
        console.log(err)
      })
    }
  },[character])

  return (
    <div className='w-screen h-5/6 grid content-between bg-[#FCF5E5]'>
      <button className='bg-white w-min justify-self-center mt-2 rounded p-1'> Random Quote </button>
      <Quotes loading={loading} setLoading={setLoading} quote={quote} error={error}/>
      <Characters setCharacter={setCharacter} selected={character}/>
    </div>
  )
}
