import React, { useState, useRef, useEffect } from 'react';

export default function Character({ setCharacter, selected }) {
  const images = require.context('../../public/character_images', true);
  const characters = ['arya_stark','jon_snow','daenerys_targaryen','tyrion_lannister','cersei_lannister','lord_varys','oberyn_martell','petyr_baelish']
  const crown = images('./crown.png')
  const [hoverRef, isHovered] = useHover();

  return (
    <div className='flex flex-wrap justify-center justify-around self-end pb-1'>
      {characters.map((char) => {
        const sprite = images(`./${char}.png`);
        return (
            <li key={char} className={`list-none cursor-pointer h-80 grid content-end ${'basis-1/'+characters.length}`} onClick={(char) => { setCharacter(char) }}>
              {selected && selected.target.id === char ? <img src={crown} className='h-12 justify-self-center' /> : ''}
              <img src={sprite} id={char} ref={hoverRef} className={`object-cover ${isHovered ? 'h-96' : 'h-56'}`} />
            </li>
        )
      })}
    </div>
  )
}

function useHover() {
  const [value, setValue] = useState(false);

  const ref = useRef(null);

  const handleMouseOver = () => setValue(true);
  const handleMouseOut = () => setValue(false);

  useEffect(
    () => {
      const node = ref.current;
      if (node) {
        node.addEventListener("mouseover", handleMouseOver);
        node.addEventListener("mouseout", handleMouseOut);

        return () => {
          node.removeEventListener("mouseover", handleMouseOver);
          node.removeEventListener("mouseout", handleMouseOut);
        };
      }
    },
    [ref.current]);

  return [ref, value];
}
